import { Component, OnInit } from '@angular/core';
import { GamesService } from '../../services/games.service';
import { AuthService } from 'src/app/services/auth.service';
import { Usuarios } from 'src/app/interfaces/usuarios';

@Component({
  selector: 'app-hist-act',
  templateUrl: './hist-act.component.html',
  styleUrls: ['./hist-act.component.css']
})
export class HistActComponent implements OnInit {
  act: any = [];
  usuarios: Usuarios = {
    idUsuario: undefined,
    nombre: '',
    correo: '',
    contrasena: '',
    telefono: undefined,
    nombreRol: ''
  };

  constructor(private gameService: GamesService, private authService: AuthService) {}

  ngOnInit(): void {
  var datos: any = this.authService.getCurrentUser();
  this.usuarios = datos[0];
  console.log(this.usuarios.idUsuario);

  if (this.usuarios.idUsuario !== undefined) {
    this.getAct(this.usuarios.idUsuario);
  } else {
    console.error('El idUsuario es undefined');
  }
}

getAct(idUsuario: number) {
  this.gameService.gethistact(idUsuario).subscribe(
    res => {
      console.log(res);
      this.act = res;
    },
    error => console.error(error)
  );
}

  
  editact(id:string){
    console.log(id);
  }
}
<app-navigation></app-navigation>
<div class="container">

    
    <h1>Historial de Actividades</h1>
    <br>
    <table class="table">
        <thead class="table table-striped">
            <tr>
                <th>Actividad</th>
                <th>Descripción</th>
                <th>Encargado</th>
                <th>Participantes</th>
                <th>Fecha de inicio</th>
                <th>Fecha de fin</th>
                <th>Estatus</th>

            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let item of act">
                <td >{{item.Nombre_Actividad}}</td>
             <td>{{item.Descripcion}}</td>
              <td>{{item.Encargado}}</td>
              <td>{{item.Participante}}</td>
              <td>{{item.Fecha_de_inicio | date: 'yyyy-MM-dd'}}</td>
              <td>{{item.Fecha_de_fin | date: 'yyyy-MM-dd'}}</td>
              <td>{{item.Estatus}}</td>

           
            <!-- Agregar más filas según sea necesario -->
        </tbody>
    </table>
    <div class="d-flex justify-content-end mt-3">
        <button type="button" class="btn btn-primary" routerLink="/gestion" > Listo</button>
      </div>
</div>

