import { Component, OnInit } from '@angular/core';
import { Activities } from 'src/app/interfaces/actividades';
import {GamesService} from '../../services/games.service';
import { AuthService } from 'src/app/services/auth.service';
import { Usuarios } from 'src/app/interfaces/usuarios';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {
  user: any = [];

  usuarios: Usuarios= {
    idUsuario: undefined,
    nombre : '',
    correo: '',
    contrasena : '' ,
    telefono : '',
    nombreRol  : ''
  }

  constructor(private gameService: GamesService, private authService: AuthService){}
  ngOnInit(): void {
    var datos:any =this.authService.getCurrentUser();
    this.usuarios = datos[0];
    console.log(this.usuarios);
  }
  
  

  editUser(id:string){
    console.log(id);
  }
}
<app-navigation></app-navigation>
<div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-8">
            <div class="card" *ngIf="usuarios">
                <div class="card-header bg-primary text-white">
                    <h4>Mi Perfil</h4>
                </div>
                <div class="card-body">
                    <button class="btn btn-outline-primary btn-sm float-right" [routerLink]="['/edituser', usuarios.idUsuario]" >Editar</button> <!-- Botón de edición -->
                    <div class="form-group">
                        <label for="nombre">Nombre:</label>
                        <p id="nombre">{{usuarios.nombre}}</p>
                    </div>
                    <div class="form-group">
                        <label for="correo">Correo Electrónico:</label>
                        <p id="correo">{{usuarios.correo}}</p>
                    </div>
                    <div class="form-group">
                        <label for="telefono">Teléfono:</label>
                        <p id="telefono">{{usuarios.telefono}}</p>
                    </div>
                    <div class="form-group">
                        <label for="nombreRol">Rol:</label>
                        <p id="nombreRol">{{usuarios.nombreRol}}</p>
                    </div>
                    <a href="home" class="btn btn-danger">Cerrar Sesión</a>
                </div>
            </div>
        </div>
    </div>
</div>
