import { Component, OnInit,HostBinding } from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { Usuarios } from 'src/app/interfaces/usuarios';
import {GamesService} from '../../services/games.service';

@Component({
  selector: 'app-edit-user',
  templateUrl: './edit-user.component.html',
  styleUrls: ['./edit-user.component.css']
})
export class EditUserComponent {
  rol: any = [];
  constructor(private gameService: GamesService, private router: Router, private activateRouter : ActivatedRoute){}
  ngOnInit(): void {
    this.gameService.getrol().subscribe(
      res => {
        console.log(res),
        this.rol = res
      },
      rep => console.error
      );

      const params1 = this.activateRouter.snapshot.params;
             if(params1['id']){
              this.gameService.getuse(params1['id'])
              .subscribe(
                res => {
                  console.log(res)
                  this.usuarios = res;
                },
                err => console.error(err)
              )
             }
  }
  @HostBinding('class') classes = 'row';

  usuarios: Usuarios= {
    idUsuario: undefined,
    nombre : '',
    correo: '',
    contrasena : '' ,
    telefono : '',
    nombreRol  : ''
  }

  updateuser(){
    this.gameService.updateuser(this.usuarios.idUsuario,this.usuarios)
    .subscribe(
      res => { 
        console.log(res);
        alert("Usuario Actualizado");
        this.router.navigate(['/user']);
      },
      err => console.error(err)
    );
  }


  
}
<app-navigation></app-navigation>
<div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <form>
                <div class="form-outline mb-4">
                  <label class="form-label" for="form2Example17">Nombre</label>
                    <input type="text" id="form2Example17" class="form-control form-control-lg" [ngModelOptions]="{ standalone: true }" [(ngModel)]="usuarios.nombre"/>
                  </div>
                <div class="form-outline mb-4">
                  <label class="form-label" for="form2Example17">Correo Electronico</label>
                  <input type="email" id="form2Example17" class="form-control form-control-lg"[ngModelOptions]="{ standalone: true }"  [(ngModel)]="usuarios.correo"/>
                </div>

                <div class="form-outline mb-4">
                  <label class="form-label" for="form2Example27">Contraseña</label>
                  <input type="text" id="form2Example27" class="form-control form-control-lg" [ngModelOptions]="{ standalone: true }" [(ngModel)]="usuarios.contrasena"/>
                </div>
                <div class="form-outline mb-4">
                  <label class="form-label" for="form2Example27">Telefono</label>
                    <input type="text" id="form2Example27" class="form-control form-control-lg" [ngModelOptions]="{ standalone: true }" [(ngModel)]="usuarios.telefono"/>
                  </div>
                  <div class="form-outline mb-4">
                    <label class="form-label" for="form2Example17">Elige tu Rol</label>
                
                        <select class="form-control" id="combobox" [(ngModel)]="usuarios.nombreRol" [ngModelOptions]="{ standalone: true }">
                            <option  *ngFor ="let item1 of rol" value="{{item1.nombreRol}}">{{item1.nombreRol}}</option>
                        </select>
                </div>
                <button type="submit" class="btn btn-primary" (click)="updateuser()">Actualizar</button>
                
            </form>
        </div>
    </div>
</div>
